import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Map from './components/Map';
import ReportForm from './components/ReportForm';
import Dashboard from './components/Dashboard';
import SocialFeed from './components/SocialFeed';
import './styles/App.css';

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [activeTab, setActiveTab] = useState('map');
  const [reports, setReports] = useState([]);
  const [socialPosts, setSocialPosts] = useState([]);

  useEffect(() => {
    if (token) {
      // Fetch user data
      fetchUserData();
      // Fetch reports
      fetchReports();
      // Fetch social posts
      fetchSocialPosts();
    }
  }, [token]);

  const fetchUserData = async () => {
    try {
      // In a real app, you'd have an endpoint to get user data
      // For now, we'll just decode the token
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUser({ id: payload.id, username: payload.username, role: payload.role });
    } catch (error) {
      console.error('Error fetching user data:', error);
      logout();
    }
  };

  const fetchReports = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reports');
      const data = await response.json();
      setReports(data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  const fetchSocialPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/social/posts', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      setSocialPosts(data);
    } catch (error) {
      console.error('Error fetching social posts:', error);
    }
  };

  const login = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    localStorage.setItem('token', authToken);
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <div className="App">
      <Header user={user} onLogout={logout} activeTab={activeTab} setActiveTab={setActiveTab} />
      
      <div className="main-content">
        {!user ? (
          <div className="auth-forms">
            <div className="form-container">
              <h2>Login</h2>
              <form onSubmit={async (e) => {
                e.preventDefault();
                const formData = new FormData(e.target);
                const email = formData.get('email');
                const password = formData.get('password');
                
                try {
                  const response = await fetch('http://localhost:5000/api/auth/login', {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ email, password })
                  });
                  
                  const data = await response.json();
                  if (response.ok) {
                    login(data.user, data.token);
                  } else {
                    alert(data.message);
                  }
                } catch (error) {
                  console.error('Login error:', error);
                }
              }}>
                <input type="email" name="email" placeholder="Email" required />
                <input type="password" name="password" placeholder="Password" required />
                <button type="submit">Login</button>
              </form>
            </div>
            
            <div className="form-container">
              <h2>Register</h2>
              <form onSubmit={async (e) => {
                e.preventDefault();
                const formData = new FormData(e.target);
                const username = formData.get('username');
                const email = formData.get('email');
                const password = formData.get('password');
                const role = formData.get('role');
                
                try {
                  const response = await fetch('http://localhost:5000/api/auth/register', {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ username, email, password, role })
                  });
                  
                  const data = await response.json();
                  if (response.ok) {
                    login(data.user, data.token);
                  } else {
                    alert(data.message);
                  }
                } catch (error) {
                  console.error('Registration error:', error);
                }
              }}>
                <input type="text" name="username" placeholder="Username" required />
                <input type="email" name="email" placeholder="Email" required />
                <input type="password" name="password" placeholder="Password" required />
                <select name="role" required>
                  <option value="citizen">Citizen</option>
                  <option value="official">Official</option>
                  <option value="analyst">Analyst</option>
                </select>
                <button type="submit">Register</button>
              </form>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'map' && (
              <div className="map-container">
                <Map reports={reports} socialPosts={socialPosts} />
                <ReportForm onReportSubmit={fetchReports} token={token} />
              </div>
            )}
            
            {activeTab === 'dashboard' && (
              <Dashboard reports={reports} userRole={user.role} />
            )}
            
            {activeTab === 'social' && (
              <SocialFeed posts={socialPosts} token={token} />
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default App;
